import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import '../../../../help/Colors.dart';import '../../../../help/text_style.dart';class Button_1 extends StatefulWidget {  const Button_1({Key? key, required this.text, required this.onPressed, required this.color}) : super(key: key);  final String text;  final void Function() onPressed;  final Color color;  @override  State<Button_1> createState() => _Button_1State();}class _Button_1State extends State<Button_1> {  @override  Widget build(BuildContext context) {    return Container(      margin: EdgeInsetsDirectional.only(top: 15,bottom: 10),        width: MediaQuery.of(context).size.width / 7,        height: 55,        child: ElevatedButton(          style: ElevatedButton.styleFrom(            backgroundColor: widget.color,            shape: RoundedRectangleBorder(                borderRadius: BorderRadius.circular(18)),          ),          onPressed: () {            widget.onPressed();          },          child: Padding(            padding: const EdgeInsets.only(top: 5),            child: Column(              mainAxisAlignment: MainAxisAlignment.center,              children: [                Row(                  mainAxisAlignment: MainAxisAlignment.center,                  children: [                    Text(                      widget.text,                      style: Text_Style.getstyle(                          fontsize:                          MediaQuery.of(context).size.longestSide /                              60,                          ColorText: Colorsapp.White,                          fontWeight: FontWeight.w800),                    ),                  ],                ),              ],            ),          ),        ));  }}