import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:get/get_rx/src/rx_types/rx_types.dart';import '../../../help/Colors.dart';import '../../../help/text_style.dart';class Home_Button_big extends StatelessWidget {  final void Function() onPressed;  const Home_Button_big({    Key? key,    required this.Text_Button,    required this.onPressed,    required this.X,  }) : super(key: key);  final String Text_Button;  final RxInt X;  @override  Widget build(BuildContext context) {    return Container(      margin: EdgeInsets.only(bottom: 15),      height: 50,      width: MediaQuery.of(context).size.width / 2,      child: Obx(() {        return ElevatedButton(          onHover: (value) {            X.value = 65;            Future.delayed(const Duration(milliseconds: 500), () {              X.value = 55;            });          },          child: Padding(            padding: const EdgeInsets.only(top: 10),            child: Text(Text_Button),          ),          onPressed: () {            onPressed();          },          style: ElevatedButton.styleFrom(              shape: RoundedRectangleBorder(                  borderRadius: BorderRadius.circular(18)              ),              primary: Colorsapp.SecondColor,              textStyle: Text_Style.getstyle(                  fontsize: MediaQuery.of(context).size.longestSide / X.value,                  ColorText: Colorsapp.mainColor,                  fontWeight: FontWeight.w700)),        );      }),    );  }}