import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import '../../../help/Colors.dart';import '../../../help/text_style.dart';class Home_Button_small extends StatelessWidget {  const Home_Button_small ({    Key? key,    required this.Text_Button,    required this.onPressed, required this.X,  }) : super(key: key);  final String Text_Button;  final void Function() onPressed;  final RxInt X ;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.all(5.0),      child: Container(        height:48,        width: MediaQuery.of(context).size.width / 1.5,        child: Obx(          () {            return ElevatedButton(              child: Text(Text_Button),              onPressed: () {                onPressed();              },              style: ElevatedButton.styleFrom(                  shape: RoundedRectangleBorder(                      borderRadius: BorderRadius.circular(18.0)                  ),                  primary: Colorsapp.SecondColor,                  textStyle: Text_Style.getstyle(                      fontsize: MediaQuery.of(context).size.longestSide / X.value,                      ColorText: Colorsapp.mainColor,                      fontWeight: FontWeight.w700)),            );          }        ),      ),    );  }}