import 'dart:ui';import 'package:email_validator/email_validator.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:get/get_rx/src/rx_types/rx_types.dart';import 'package:google_sign_in/google_sign_in.dart';import '../../../controler/method.dart';import '../../../help/Colors.dart';import '../FirstPage/First_page_large.dart';import '../FirstPage/Home_Button_big.dart';import 'News_page/News_larg_Page.dart';import 'Schedules_page_page/Schedules_larg_Page.dart';class Post_main_page extends StatefulWidget {  Post_main_page({Key? key}) : super(key: key);  @override  State<Post_main_page> createState() => _Post_main_pageState();}class _Post_main_pageState extends State<Post_main_page> {  @override  void initState() {    Methods.getNews();    Methods.getSchedules();    super.initState();  }  @override  Widget build(BuildContext context) {    final PageController controller = PageController(initialPage:0);    return Scaffold(        body: PageView(      /// [PageView.scrollDirection] defaults to [Axis.horizontal].      /// Use [Axis.vertical] to scroll vertically.      scrollDirection: Axis.horizontal,      controller: controller,      children: <Widget>[        MediaQuery.of(context).size.longestSide > 1000            ? Container(                height: MediaQuery.of(context).size.height,                width: MediaQuery.of(context).size.width,                decoration: const BoxDecoration(                  image: DecorationImage(                      image: AssetImage("photo/Webpage6.png"),                      fit: BoxFit.cover),                ),                child: Post_larg_Page(),              )            : Container(                height: MediaQuery.of(context).size.height,                width: MediaQuery.of(context).size.width,                decoration: const BoxDecoration(                  image: DecorationImage(                      image: AssetImage("photo/Webpage1.png"),                      fit: BoxFit.cover),                ),                child: Post_larg_Page(),              ),////////////////////// Schedules ///////////////////////////        MediaQuery.of(context).size.longestSide > 1000            ? Container(                height: MediaQuery.of(context).size.height,                width: MediaQuery.of(context).size.width,                decoration: const BoxDecoration(                  image: DecorationImage(                      image: AssetImage("photo/Webpage6.png"),                      fit: BoxFit.cover),                ),                child: Schedules_larg_Page(),              )            : Container(                height: MediaQuery.of(context).size.height,                width: MediaQuery.of(context).size.width,                decoration: const BoxDecoration(                  image: DecorationImage(                      image: AssetImage("photo/Webpage1.png"),                      fit: BoxFit.cover),                ),                child: Schedules_larg_Page(),              ),      ],    ));  }}class AppScrollBehavior extends MaterialScrollBehavior {  @override  Set<PointerDeviceKind> get dragDevices => {        PointerDeviceKind.touch,        PointerDeviceKind.mouse,      };}/*MediaQuery.of(context).size.longestSide > 1000?            Container(              height: MediaQuery.of(context).size.height,              width: MediaQuery.of(context).size.width,              decoration: const BoxDecoration(                image: DecorationImage(                    image: AssetImage("photo/Webpage6.png"), fit: BoxFit.cover),              ),              child:Post_larg_Page(),            ):            Container(              height: MediaQuery.of(context).size.height,              width: MediaQuery.of(context).size.width,              decoration: const BoxDec