import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import '../../../../help/Colors.dart';import '../../../../help/text_style.dart';class Schedules_Button1 extends StatelessWidget {  const Schedules_Button1({    Key? key,    required this.changecolor,    required this.onPressed,    required this.text1,    required this.text2,  }) : super(key: key);  final bool changecolor;  final void Function() onPressed;  final String text1;  final String text2;  @override  Widget build(BuildContext context) {    return ElevatedButton(      style: ElevatedButton.styleFrom(        backgroundColor: changecolor == false            ? Colorsapp.SecondColor            : Colorsapp.mainColor,        shape: RoundedRectangleBorder(            borderRadius: BorderRadius.circular(18)),      ),      onPressed: () {        onPressed();      },      child: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          Text(            textDirection: TextDirection.rtl,            "$text1",            style: Text_Style.getstyle(                fontsize: MediaQuery.of(context).size.longestSide / (MediaQuery.of(context).size.longestSide > 1000?85:65),                ColorText: Colorsapp.White,                fontWeight: FontWeight.w800),          ),          if (text2 != "")            Text(              textDirection: TextDirection.ltr,              "$text2",              style: Text_Style.getstyle(                  fontsize:                  MediaQuery.of(context).size.longestSide / (MediaQuery.of(context).size.longestSide > 1000?85:65),                  ColorText: Colorsapp.White,                  fontWeight: FontWeight.w800),            ),        ],      ),    );  }}