import 'package:flutter/material.dart';import '../../../../help/Colors.dart';import '../../../../help/text_style.dart';class Send_news_TextField extends StatelessWidget {  const Send_news_TextField({    Key? key,    required this.news,  }) : super(key: key);  final TextEditingController news;  @override  Widget build(BuildContext context) {    return Container(        width: MediaQuery.of(context).size.width / 2,        height: MediaQuery.of(context).size.height / 4.5,        child: Form(          child: TextFormField(              maxLines: null,              minLines: null,              expands: true,              obscureText: false,              style: Text_Style.getstyle(                  fontsize: 20,                  ColorText: Colors.black,                  fontWeight: FontWeight.w800),              textAlign: TextAlign.end,              textDirection: TextDirection.rtl,              keyboardType: TextInputType.multiline,              decoration: InputDecoration(                  hintTextDirection: TextDirection.rtl,                  fillColor: Colorsapp.TextColor,                  filled: true,                  border: OutlineInputBorder(                    borderRadius: BorderRadius.circular(18),                    borderSide:                        BorderSide(color: Colorsapp.mainColor, width: 2),                  ),                  enabledBorder: OutlineInputBorder(                    borderRadius: BorderRadius.circular(18.0),                    borderSide: BorderSide(                      width: 2,                      color: Colorsapp.White,                    ),                  ),                  hintText: "اضافة خبر :",                  hintStyle: Text_Style.getstyle(                      fontsize: 20,                      ColorText: Colors.black45,                      fontWeight: FontWeight.w800),                  contentPadding:                      const EdgeInsets.all(10)),              controller: news,              onChanged: (text) {},              validator: (value) {}),        ));  }}